import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/home.css";

function Home() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Load images dynamically from 1.jpeg to 9.jpeg
    const sampleImages = Array.from({ length: 9 }, (_, index) => `/assets/images/${index + 1}.jpeg`);
    setImages(sampleImages);
  }, []);

  return (
    <div className="home">
      <div className="top-nav-container">
        <nav className="top-nav">
            <Link to="/" className="nav-item">HOME</Link>
            <Link to="/collections" className="nav-item">SERVICES</Link>
            <Link to="/work" className="nav-item-black">OUR WORK</Link>
            <Link to="/contact" className="nav-item-black">ONBOARDING</Link>
            <Link to="/contact" className="nav-item-black">CONTACT</Link>
          </nav>
        <div className="logo-container">
          <img src="/assets/maeknit-no-bg-logo.png" alt="Logo" className="logo" />
        </div>
      </div>
      <div className="video-container">
        <video autoPlay loop muted className="background-video">
          <source src="/assets/ml_v1.mp4" type="video/mp4" />
        </video>
        <div className="centered-text-container">
          <img src="/assets/images/mfaw.png" alt="Centered Text" className="centered-text" />
        </div>
      </div>

    </div>
  );
}

export default Home;
