import React, { useEffect, useState } from "react";
import "../styles/home.css";

function Home() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Example images, replace with dynamic sources
    const sampleImages = [
      "/assets/about.webp",
      "/assets/image2.jpg",
      "/assets/image3.jpg",
      "/assets/image4.jpg",
      "/assets/image5.jpg",
      "/assets/image6.jpg",
    ];
    setImages([...sampleImages, ...sampleImages]); // Duplicate for seamless scrolling
  }, []);

  return (
    <div className="home">
      <video autoPlay loop muted className="background-video">
        <source src="/assets/video.mov" type="video/mp4" />
      </video>
      <div className="infinite-scroll-container">
        <div className="scrolling-content">
          {images.map((src, index) => (
            <img key={index} src={src} alt={`Scroll ${index}`} className="scroll-item" />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
