import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/navbar.css";

function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <div className="top-nav-container">
      {/* Menu Icon (Visible on Mobile) */}
      <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
        {menuOpen ? "✖" : "☰"}
      </div>

      {/* Logo */}
      <div className="logo-container">
        <img src="/assets/maeknit-no-bg-logo.png" alt="Logo" className="logo" />
      </div>

      {/* Navigation Menu */}
      <nav className={`top-nav ${menuOpen ? "active" : ""}`}>
        <ul>
          <li><Link to="/" className="nav-item-black" onClick={() => setMenuOpen(false)}>HOME</Link></li>
          <li><Link to="/services" className="nav-item-black" onClick={() => setMenuOpen(false)}>SERVICES</Link></li>
          <li><Link to="/work" className="nav-item-black" onClick={() => setMenuOpen(false)}>OUR WORK</Link></li>
          <li><Link to="/about" className="nav-item-black" onClick={() => setMenuOpen(false)}>ABOUT</Link></li>
          <li><Link to="/contact" className="nav-item-black" onClick={() => setMenuOpen(false)}>CONTACT</Link></li>
        </ul>
      </nav>
    </div>
  );
}

export default Navbar;
