import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/navbar.css";

function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <div className="top-nav-container">
      <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
        {menuOpen ? "✖" : "☰"} {/* Toggle between three-line and close icon */}
      </div>
      <nav className={`top-nav ${menuOpen ? "active" : ""}`}>
        <Link to="/contact" className="nav-item-black" onClick={() => setMenuOpen(false)}>CONTACT</Link>
        <Link to="/about" className="nav-item-black" onClick={() => setMenuOpen(false)}>ABOUT</Link>
        <Link to="/work" className="nav-item-black" onClick={() => setMenuOpen(false)}>OUR WORK</Link>
        <Link to="/services" className="nav-item-black" onClick={() => setMenuOpen(false)}>SERVICES</Link>
        <Link to="/" className="nav-item-black" onClick={() => setMenuOpen(false)}>HOME</Link>
      </nav>
      <div className="logo-container">
        <img src="/assets/maeknit-no-bg-logo.png" alt="Logo" className="logo" />
      </div>

    </div>
  );
}

export default Navbar;
