import React from "react";
import { Link, useLocation } from "react-router-dom";
import "../styles/navbar.css";

function Navbar() {
  const location = useLocation();

  // Define navigation items
  const navItems = [
    { path: "/", label: "Home" },
    { path: "/about", label: "About" },
    { path: "/services", label: "Services" },
    { path: "/work", label: "Work" },
    { path: "/onboarding", label: "Onboarding" },
    { path: "/contact", label: "Contact" },
  ];

  // Determine navbar text color based on current page
  const isHomePage = location.pathname === "/";

  return (
    <div className={`navbar-container ${isHomePage ? "home-navbar" : "default-navbar"}`}>
      <div className="logo-container">
        <img src="/assets/maeknit-no-bg-logo.png" alt="Logo" className="logo" />
      </div>
      <nav className="nav-links">
        {navItems
          .filter((item) => item.path !== location.pathname) // Exclude current page
          .map((item) => (
            <Link key={item.path} to={item.path} className="nav-item">
              {item.label}
            </Link>
          ))}
      </nav>
    </div>
  );
}

export default Navbar;
