import React, { useEffect, useState } from "react";
import "../styles/home.css";

function Home() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Example images, replace with dynamic sources
    const sampleImages = Array.from({ length: 9 }, (_, index) => `/assets/image${index % 10 + 1}.jpg`);
    setImages(sampleImages);
  }, []);

  return (
    <div className="home">
      <video autoPlay loop muted className="background-video">
        <source src="/assets/video.mov" type="video/mp4" />
      </video>
      <div className="image-grid-container">
        {images.map((src, index) => (
          <img key={index} src={src} alt={`Gallery ${index}`} className={`grid-item grid-item-${index % 4}`} />
        ))}
      </div>
    </div>
  );
}

export default Home;
