import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/home.css";

function Home() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Load images dynamically from 1.jpeg to 9.jpeg
    const sampleImages = Array.from({ length: 9 }, (_, index) => `/assets/images/${index + 1}.jpeg`);
    setImages(sampleImages);
  }, []);

  return (
    <div className="home">
      <div className="top-nav-container">
        <nav className="top-nav">
          <Link to="/about" className="nav-item">About</Link>
          <Link to="/collections" className="nav-item">Collections</Link>
          <Link to="/gallery" className="nav-item">Gallery</Link>
          <Link to="/contact" className="nav-item">Contact</Link>
        </nav>
        <div className="logo-container">
          <img src="/assets/maeknit-no-bg-logo.png" alt="Logo" className="logo" />
        </div>
      </div>
      <div className="centered-text-container">
        <img src="/assets/text-image.png" alt="Centered Text" className="centered-text" />
      </div>
      <video autoPlay loop muted className="background-video">
        <source src="/assets/video.mov" type="video/mp4" />
      </video>
      <div className="image-scroll-grid-container">
        {images.map((src, index) => (
          <div key={index} className={`grid-item grid-item-${index % 3}`}>
            <img src={src} alt={`Gallery ${index + 1}`} className="grid-image" />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
